import React, { Component } from "react"

export default class RenderizadoElmentos extends Component {
    constructor(props) {
        super(props);
        this.state = {
            seasons: ["Primavera", "Verano", "Otoño", "Invierno"]
        }
    }
    render() {
        return (
            <div>
                <h2>Renderizado de Elementos</h2>
                <h3>Estaciones del Año:</h3>
                <ol>
                    {
                        this.state.seasons.map((el) => <li>
                            {el}
                        </li>)
                    }
                </ol>
            </div>
        )
    }
}
